mapper.insert:

   SELECT (IFNULL(MAX(SN), 0)+1) SN FROM DEVICE



	INSERT INTO DEVICE 
		( SN
		  , UUID
		  , OS
		  , TELNO
		  , STRGE_INFO
		  , NTWRK_DEVICE_INFO
		  , PG_VER
		  , DEVICE_NM
		  , USEYN )
	VALUES ( #{sn}
		  , #{uuid}
		  , #{os}
		  , #{telno}
		  , #{strgeInfo}
		  , #{ntwrkDeviceInfo}
		  , #{pgVer}
		  , #{deviceNm}
		  , 'Y')

mapper.select:
SELECT COUNT(*) totcnt
FROM DEVICE
WHERE UUID=#{uuid}
