mapper.insert:

   SELECT (IFNULL(MAX(SN), 0)+1) sn from PUSH_DEVICE



	INSERT INTO PUSH_DEVICE
		(SN,
			UUID,
			NTWRK_DEVICE_INFO,
			OS_VER,
			DEVICE_NM,
			USEYN,
			OS_TYPE,
			TOKEN_ID)
	VALUES ( #{sn}
		  , #{uuid}
		  , #{ntwrkDeviceInfo}
		  , #{osVer}
		  , #{deviceNm}
		  , #{useYn}
		  , #{osType}
		  , #{tokenId})


   SELECT (IFNULL(MAX(SN), 0)+1) sn from PUSH_MESSAGE



	INSERT INTO PUSH_MESSAGE
		(SN,
			UUID,
			MESSAGE,
			SND_DT,
			TOKEN_ID)
	VALUES ( #{sn}
		  , #{uuid}
		  , #{message}
		  , #{sndDt}
		  , #{tokenId})
