mapper.insert:

   SELECT (NVL(MAX(SN), 0)+1) sn from ACCELERATOR



	INSERT INTO ACCELERATOR 
		( SN
		  , UUID
		  , XAXIS
		  , YAXIS
		  , ZAXIS
		  , "TIMESTAMP"
		  , USEYN )
	VALUES ( #{sn}
		  , #{uuid}
		  , #{xaxis}
		  , #{yaxis}
		  , #{zaxis}
		  , #{timestamp}
		  , #{useYn})

mapper.update:

UPDATE ACCELERATOR 
SET SN=#{sn}
	, UUID=#{uuid}
	, XAXIS=#{xaxis}
	, YAXIS=#{yaxis}
	, ZAXIS=#{zaxis}
	, TIMESTAMP=#{timestamp}
	, USEYN=#{useYn}
WHERE SN=#{sn}
