mapper.insert:

   SELECT (IFNULL(MAX(SN), 0)+1) sn from COMPASS



	INSERT INTO COMPASS 
		( SN
		  , UUID
		  , DRC
		  , ACCRCY
		  , TIMESTAMP
		  , USEYN )
	VALUES ( #{sn}
		  , #{uuid}
		  , #{drc}
		  , #{accrcy}
		  , #{timestamp}
		  , #{useYn})

mapper.update:

UPDATE COMPASS 
SET SN=#{sn}
	, UUID=#{uuid}
	, DRC=#{drc}
	, ACCRCY=#{accrcy}
	, TIMESTAMP=#{timestamp}
	, USEYN=#{useYn}
WHERE SN=#{sn}
