		  , FILE_SIZE
		  , USEYN )
	VALUES ( #{sn}
		  , #{uuid}
		  , #{fileSn}
		  , #{fileNm}
		  , #{fileStreCours}
		  , #{fileType}
		  , SYSDATE
		  , #{fileSize}
		  , #{useYn})



